INSERT INTO `pages` (`id`, `pageid`, `title`, `plain_text`, `img_src`, `link`, `size`, `wordcount`, `snippet`) VALUES
(1, 25019, 'Липецк', 'palin_text', 'https://upload.wikimedia.org/wikipedia/commons/c/c5/%D0%9B%D0%B8%D0%BF%D0%B5%D1%86%D0%BA_2016.jpg', 'https://ru.wikipedia.org/wiki/Липецк', 193695, 11627, '<span class=\"searchmatch\">Ли?пецк</span> — город (с 1779 года) в России, административный центр <span class=\"searchmatch\">Липецкой</span> области. Является ядром крупнейшей российской агломерации со специализацией в'),
(2, 2944, 'Пётр I', 'palin_text', 'https://upload.wikimedia.org/wikipedia/commons/4/4a/Jean-Marc_Nattier%2C_Pierre_Ier_%281717%29_-002.jpg', 'https://ru.wikipedia.org/wiki/Пётр I', 205210, 12396, '<span class=\"searchmatch\">Пётр</span> <span class=\"searchmatch\">I</span> Алексе?евич, прозванный Вели?ким (30 мая [9 июня] 1672 года — 28 января [8 февраля] 1725 года) — последний царь всея Руси (с 1682 года) и первый'),
(3, 176511, 'Липа', 'palin_text', 'https://upload.wikimedia.org/wikipedia/commons/a/a7/HoeveltegaardHovedbygning-DSC_7583-530px.jpg', 'https://ru.wikipedia.org/wiki/Липа', 37475, 2235, 'видов <span class=\"searchmatch\">лип</span>. В европейской России и в Западной Сибири распространён вид <span class=\"searchmatch\">Липа</span> мелколистная. В Сибири, кроме него, встречаются <span class=\"searchmatch\">Липа</span> сибирская и <span class=\"searchmatch\">Липа</span> Нащокина');
